,\n            \"right\": \"desc_component\"\n          },\n          {\n            \"left\": \"html\",\n            \"right\": \"desc_html\"\n          },\n          {\n            \"left\": \"css\",\n            \"right\": \"desc_css\"\n          },\n          {\n            \"left\": \"ts\",\n            \"right\": \"desc_ts\"\n          }\n        ]\n      }\n    ],\n    \"imageUrl\": \"link\",\n    \"backgroundColor\": \"#FFF3E0\",\n    \"audioUrl\": \"link\",\n    \"avatar\": {\n      \"name\": \"Lina\",\n      \"imageUrl\": \"link\",\n      \"position\": \"bottomLeft\"\n    }\n  }" },// code snippet
