                                new LessonDTO
                                {
                                    Id = 1003,
                                    Title = "Web Optimization",
                                    Description = "Best options for server render and web optimization in our project.",
                                    Order = 3,
                                    StageId = 1,
                                    LessonContents     =
                                    [
                                        new LessonContentDTO { Id = 1013, LessonId = 1003, TypeId = 1, Order = 1, Content = "{\n  \"title\": {\n    \"en\": \"Choosing a Stylesheet Format\",\n    \"es\": \"Eligiendo un Formato de Hoja de Estilos\"\n  },\n  \"textContents\": {\n    \"introduction\": [\n      {\n        \"en\": \"When starting a web project, you’ll see the next question. Let’s review it\",\n        \"es\": \"Al iniciar un proyecto web, verás la siguiente pregunta. Vamos a revisarlo.\"\n      }\n    ],\n    \"contents\": [\n      {\n        \"en\": \"**Which** **stylesheet format** would you like to use?\",\n        \"es\": \"**¿Qué** formato de **hoja de estilos** te gustaría usar?\"\n      },\n      {\n        \"en\": \"Stylesheet formats are different ways of languages to write CSS in web projects.\",\n        \"es\": \"Los formatos de hojas de estilos son diferentes formas o lenguajes para escribir estilos CSS en proyectos web.\"\n      },\n      {\n        \"en\": \"Some formats like **SCSS** or **LESS** offer extra **features** that make writing styles easier and more powerful.\",\n        \"es\": \"Algunos formatos de hojas de estilos (como **SCSS** o **LESS**) tienen **herramientas** extra que hacen más fácil y potente escribir estilos.\"\n      }\n    ]\n  },\n  \"vocabulary\": [\n    {\n      \"term\": {\n        \"en\": \"which\",\n        \"es\": \"qué\"\n      },\n      \"definition\": {\n        \"en\": \"Used to ask about one or more options from a defined set.\",\n        \"es\": \"Se usa para preguntar sobre una o más opciones dentro de un conjunto definido.\"\n      }\n    },\n    {\n      \"term\": {\n        \"en\": \"stylesheet format\",\n        \"es\": \"formato de hoja de estilos\"\n      },\n      \"definition\": {\n        \"en\": \"A specific way or language used to write CSS styles, such as SCSS or LESS.\",\n        \"es\": \"Una forma o lenguaje específico para escribir estilos CSS, como SCSS o LESS.\"\n      }\n    },\n    {\n      \"term\": {\n        \"en\": \"SCSS\",\n        \"es\": \"SCSS\"\n      },\n      \"definition\": {\n        \"en\": \"A CSS preprocessor that adds features like variables, nesting, and mixins. It uses a syntax fully compatible with standard CSS.\",\n        \"es\": \"Un preprocesador de CSS que añade funciones como variables, anidamiento y mixins. Utiliza una sintaxis totalmente compatible con el CSS estándar.\"\n      }\n    },\n    {\n      \"term\": {\n        \"en\": \"LESS\",\n        \"es\": \"LESS\"\n      },\n      \"definition\": {\n        \"en\": \"A CSS preprocessor that extends CSS with dynamic behavior such as variables, mixins, and functions. Its syntax is inspired by JavaScript, offering flexibility but less direct compatibility with plain CSS.\",\n        \"es\": \"Un preprocesador de CSS que extiende CSS con comportamientos dinámicos como variables, mixins y funciones. Su sintaxis está inspirada en JavaScript, lo que ofrece flexibilidad pero menos compatibilidad directa con CSS puro.\"\n      }\n    },\n    {\n      \"term\": {\n        \"en\": \"features\",\n        \"es\": \"herramientas\"\n      },\n      \"definition\": {\n        \"en\": \"Extra capabilities or functions that improve how something works.\",\n        \"es\": \"Capacidades o funciones adicionales que mejoran cómo funciona algo.\"\n      }\n    },\n    {\n      \"term\": {\n        \"en\": \"Search Engine Optimization\",\n        \"es\": \"Optimización para Motores de Búsqueda\"\n      },\n      \"definition\": {\n        \"en\": \"A set of techniques used to improve a website’s visibility in search engine results.\",\n        \"es\": \"Conjunto de técnicas utilizadas para mejorar la visibilidad de un sitio web en los resultados de los motores de búsqueda.\"\n      }\n    }\n  ]\n}" },
                                         new LessonContentDTO { Id = 1014, LessonId = 1003, TypeId = 1, Order = 2, Content = " {\n    \"title\": {\n      \"en\": \"Web Optimization with SSR and SSG\",\n      \"es\": \"Optimización Web con SSR y SSG\"\n    },\n    \"textContents\": {\n      \"introduction\": [\n        {\n          \"en\": \"When creating a project, the following question may appear. \",\n          \"es\": \"Al momento de crear un proyecto, probablemente ha de salir las siguiente pregunta. \"\n        }\n      ],\n  \n      \"contents\": [\n        {\n          \"en\": \"Do you want to enable Server-Side Rendering (SSR) and Static Site Generation (SSG/Prerendering)?\",\n          \"es\": \"¿Quieres habilitar el Renderizado en el Servidor (SSR) y la Generación Estática de Sitios (SSG/Prerenderizado)?\"\n        },\n        {\n          \"en\": \"This question is asking if you want your web pages to be created before someone visits them\",\n          \"es\": \"Esta pregunta quiere saber si prefieres que tus páginas web se creen antes de que alguien las visite\"\n        },\n        {\n          \"en\": \"or at the moment they visit.\",\n          \"es\": \"o justo cuando las visitan.\"\n        },\n        {\n          \"en\": \"SEO stands for **Search Engine Optimization** It is the set of techniques \",\n          \"es\": \"SEO significa **Search Engine Optimization**  Es el conjunto de técnicas \"\n        },\n        {\n          \"en\": \"— that makes your site works faster and easy to find on search engines like Google.\",\n          \"es\": \"— que hace que tu sitio funcione más rápido y  fácil de encontrar en buscadores como Google.\"\n        },\n        {\n          \"en\": \"Good SEO helps your site rank higher in search results.\",\n          \"es\": \"Un buen SEO ayuda a que tu web aparezca más alto en los resultados de búsqueda. \"\n        }\n      ]\n    },\n    \"vocabulary\": [\n      {\n        \"term\": {\n          \"en\": \"which\