                                    [
                                        new LessonContentDTO { Id = 1001, LessonId = 1001, TypeId = 1, Order = 1, Content = "{\n    \"title\": {\n      \"en\": \"Introduction to Angular\",\n      \"es\": \"Introducción a Angular \"\n    },\n    \"textContents\": {\n      \"introduction\": [\n        {\n          \"en\": \"Let\'s get familiar with the basic concepts.\",\n          \"es\": \"Vamos a familiarizarnos con los conceptos bases.  \"\n        }\n      ],\n      \"ending\": [\n        {\n          \"en\": \"Before continuing with the Angular installation, Node.js is required. If you don\'t have it installed, follow these steps:\",\n          \"es\": \"Antes de continuar con la instalación de Angular es necesario lo Node.js. Si no lo tienes estos son los pasos para la instalación:\"\n        },\n        {\n          \"en\": \"1. Go to the official website.\",\n          \"es\": \"1. Ir al sitio oficial.\"\n        },\n        {\n          \"en\": \"Visit: nodejs.org\",\n          \"es\": \"Ve a: nodejs.org \"\n        },\n        {\n          \"en\": \"2. Download the installer.\",\n          \"es\": \"2. Descargar el instalador. \"\n        },\n        {\n          \"en\": \"Choose **LTS** version and download the installer for your operating system.\",\n          \"es\": \" Elige la versión ***LTS**  y descarga el instalador para tu sistema operativo. \"\n        },\n        {\n          \"en\": \"3. Run the installer.\",\n          \"es\": \"3. Ejecutar el instalador. \"\n        },\n        {\n          \"en\": \"Double-click the downloaded file. Accept the terms and conditions. Leave the default options and click Next until the installation is complete.\",\n          \"es\": \"Haz doble clic en el archivo descargado. Acepta los términos y condiciones.Deja las opciones por defecto y haz clic en Next hasta terminar.\"\n        }\n      ],\n      \"contents\": [\n        {\n          \"en\": \"Angular is a **framework** for building web applications, based on components.\",\n          \"es\": \"Angular es un **marco de trabajo** para la realización de aplicaciones web, basado en componentes. \"\n        },\n        {\n          \"en\": \"A **component** is a part or element that is part of a larger whole.\",\n          \"es\": \"Un **componente** es una parte o elemento que forma parte de un todo más grande.\"\n        },\n        {\n          \"en\": \"In Angular, a component is made up of 3 types of files\",\n          \"es\": \"En Angular el componente se conforma de 3 elementos/tipos de archivos.\"\n        },\n        {\n          \"en\": \"CSS, HTML, and TS.\",\n          \"es\": \"CSS, HTML, Y TS. \"\n        },\n        {\n          \"en\": \"HTML structures the page of a website\",\n          \"es\": \".html es el archivo que permite estructura el cuerpo de la página web\"\n        },\n        {\n          \"en\": \"CSS is the file that adds styles to the HTML.\",\n          \"es\": \".css es el archivo que le da estilos al .html\"\n        },\n        {\n          \"en\": \"And the TS file contains the logic that gives functionality to the HTML.\",\n          \"es\": \"y el .ts contiene la lógica que permite al html tener funcionalidad.\"\n        }\n      ]\n    },\n    \"backgroundColor\": \"#FFF3E0\",\n    \"avatar\": {\n      \"name\": \"Lina\",\n      \"imageUrl\": \"link\",\n      \"position\": \"bottomLeft\"\n    },\n    \"vocabulary\": [\n  {\n    \"term\": {\n      \"en\": \"component\",\n      \"es\": \"componente\"\n    },\n    \"definition\": {\n      \"en\": \"A reusable part of an Angular app containing HTML, CSS, and logic (TS).\",\n      \"es\": \"Una parte reutilizable de una aplicación Angular que contiene HTML, CSS y lógica (TS).\"\n    }\n  },\n  {\n    \"term\": {\n      \"en\": \"framework\",\n      \"es\": \"framework\"\n    },\n    \"definition\": {\n      \"en\": \"A structure that helps in building software applications.\",\n      \"es\": \"Una estructura que facilita el desarrollo de software.\"\n    }\n  },\n  {\n    \"term\": {\n      \"en\": \"npm\",\n      \"es\": \"npm\"\n    },\n    \"definition\": {\n      \"en\": \"Node.js package manager used to install tools.\",\n      \"es\": \"Gestor de paquetes de Node.js para instalar herramientas.\"\n    }\n  },\n  {\n    \"term\": {\n      \"en\": \"Node.js\",\n      \"es\": \"Node.js\"\n    },\n    \"definition\": {\n      \"en\": \"Environment that allows JavaScript to run outside the browser.\",\n      \"es\": \"Entorno que permite ejecutar JavaScript fuera del navegador.\"\n    }\n  },\n  {\n    \"term\": {\n      \"en\": \"LTS\",\n      \"es\": \"LTS\"\n    },\n    \"definition\": {\n      \"en\": \"Long Term Support version, recommended for stable and secure installations.\",\n      \"es\": \"Versión con soporte a largo plazo, recomendada para instalaciones estables y seguras.\"\n    }\n  }\n]\n\n  }" },
                                        new LessonContentDTO { Id = 1002, LessonId = 1001, TypeId = 1, Order = 2, Content = " {\n    \"title\": {\n      \"en\": \"Node.js and npm \",\n      \"es\": \"Node.js y npm \"\n    },\n    \"textContents\": {\n      \"introduction\": [\n        {\n          \"en\": \"Before creating a project, we need to get familiar with these concepts.\",\n          \"es\": \"Antes de crear un proyecto. Debemos conocer familiarizarnos con estos conceptos \"\n        },\n        {\n          \"en\": \"Let\'s look at the following analogy. To understand better\",\n          \"es\": \"Veamos la siguiente analogía para entender mejor.\"\n        }\n      ]\n    },\n    \"tables\": [\n      {\n        \"title\": {\n          \"en\": \"Rendering Architectures\",\n          \"es\": \"Arquitecturas de renderizado.\"\n        },\n        \"tableContent\": [\n          {\n            \"element\": {\n              \"en\": \"JavaScript\",\n              \"es\": \"JavaScript\"\n            },\n            \"example\": {\n              \"en\": \"🔧 The language used to build the car\",\n              \"es\": \"🔧 El lenguaje para construir carros\"\n            },\n            \"description\": {\n              \"en\": \"The programming language\",\n              \"es\": \"Es el lenguaje de programación\"\n            }\n          },\n          {\n            \"element\": {\n              \"en\": \"Node.js\",\n              \"es\": \"Node.js\"\n            },\n            \"example\": {\n              \"en\": \"🚗 The car engine\",\n              \"es\": \"🚗 El motor del carro\"\n            },\n            \"description\": {\n              \"en\": \"Executes JavaScript outside the browser\",\n              \"es\": \"Ejecuta JavaScript fuera del navegador\"\n            }\n          },\n          {\n            \"element\": {\n              \"en\": \"npm\",\n              \"es\": \"npm\"\n            },\n            \"example\": {\n              \"en\": \"🛒 