                                        new LessonContentDTO { Id = 1005, LessonId = 1002, TypeId = 1 , Order = 1, Content ="  {\n    \"title\": {\n      \"en\": \"Understanding Command for Installing Angular\",\n      \"es\": \"Entendiendo el comando para instalar Angular\"\n    },\n    \"textContents\": {\n      \"introduction\": [\n        {\n          \"en\": \"Now we are going to install Angular.\",\n          \"es\": \"Ahora podemos instalar Angular\"\n        }\n      ],\n      \"contents\": [\n        {\n          \"en\": \"Open your system terminal and **run** the following **command**:\",\n          \"es\": \"Abrimos la terminal de nuestro sistema. Y ejecutamos el siguiente **comando**\"\n        },\n        {\n          \"en\": \"npm install -g @angular/cli\",\n          \"es\": \"npm install -g @angular/cli\"\n        },\n        {\n          \"en\": \"Based on the previous analogy, we can explain the command like this:\",\n          \"es\": \"Según la analogía pasada, podemos explicar el comando mostrado así:\"\n        },\n        {\n          \"en\": \"With Node already installed (the engine), we use npm (the store) to globally install -g Angular CLI (a tool used with JavaScript).\",\n          \"es\": \"Con Node ya instalado (el motor), usa npm (la tienda) para instalar de forma *global -g Angular CLI (una herramienta que se usa con JavaScript).\"\n        }\n      ]\n    },\n    \"vocabulary\": [\n      {\n        \"term\": {\n          \"en\": \"run\",\n          \"es\": \"ejecutar\"\n        },\n        \"definition\": {\n          \"en\": \"To execute a command, script, or application. In web development, it\'s used to start servers, processes, or tools.\",\n          \"es\": \"Ejecutar un comando, script o aplicación. En desarrollo web, se usa para iniciar servidores, procesos o herramientas.\"\n        }\n      },\n      {\n        \"term\": {\n          \"en\": \"global\",\n          \"es\": \"global\"\n        },\n        \"definition\": {\n          \"en\": \"A type of installation that makes a tool available system-wide, not just in one project.\",\n          \"es\": \"Un tipo de instalación que hace que una herramienta esté disponible en todo el sistema, no solo en un proyecto.\"\n        }\n      },\n      {\n        \"term\": {\n          \"en\": \"command\",\n          \"es\": \"comando\"\n        },\n        \"definition\": {\n          \"en\": \"An instruction you type in the terminal to perform a specific task.\",\n          \"es\": \"Una instrucción que escribes en la terminal para realizar una tarea específica.\"\n        }\n      }\n    ],\n    \"imageUrl\": \"LINK\",\n    \"backgroundColor\": \"#FFF3E0\",\n    \"avatar\": {\n      \"name\": \"Lina\",\n      \"imageUrl\": \"LINK\",\n      \"position\": \"bottomLeft\"\n    }\n  }" },
                                        new LessonContentDTO { Id = 1006, LessonId = 1002, TypeId = 2, Order = 2, Content = " {\n    \"title\": {\n      \"en\": \"Verify Angular Version \",\n      \"es\": \"Verificar versión de Angular\"\n    },\n    \"textContents\": {\n      \"ending\": [\n        {\n          \"en\": \"Congratulations! We have successfully installed Angular!\",\n          \"es\": \"¡Felicidades, hemos instalado correctamente Angular!\"\n        },\n        {\n          \"en\": \"Remember, to create a new Angular project, we use the command: ng new project-name\",\n          \"es\": \"Recuerda que para la creación de un proyecto en Angular vamos a utilizar el comando: ng new nombre-de-proyecto\"\n        }\n      ]\n    },\n    \"exercise\": [\n      {\n        \"type\": \"input\",\n        \"instruction\": {\n          \"en\": \"Let\'s verify the version of the Angular CLI. Type the correct command:\",\n          \"es\": \"Vamos a verificar la versión de nuestro cliente de Angular. Escribe el comando correcto:\"\n        },\n        \"input\": \"ng version\",\n        \"output\": \"    _                      _                 ____ _     ___\\n    / \\\\   _ __   __ _ _   _| | __ _ _ __     / ___| |   |_ _|\\n   / △ \\\\ | \'_ \\\\ / _` | | | | |/ _` | \'__|   | |   | |    | |\\n  / ___ \\\\| | | | (_| | |_| | | (_| | |      | |___| |___ | |\\n /_/   \\\\_\\\\_| |_|\\\\__, |\\\\__,_|_|\\\\__,_|_|       \\\\____|_____|___|\\n                |___/\\n\\nAngular CLI: 18.0.3\\nNode: 22.17.0\\nPackage Manager: npm 10.9.2\\nOS: win32 x64\\n\\nAngular: undefined\\n...\\n\\nPackage                      Version\\n------------------------------------------------------\\n@angular-devkit/architect    0.1800.3 (cli-only)\\n@angular-devkit/core         18.0.3 (cli-only)\\n@angular-devkit/schematics   18.0.3 (cli-only)\\n@schematics/angular          18.0.3 (cli-only)\",\n        \"eoutput\": \"Incorrect command.\",\n        \"completed\": false\n      }\n    ],\n    \"imageUrl\": \"LINK\",\n    \"backgroundColor\": \"#FFF3E0\",\n    \"avatar\": {\n      \"name\": \"Lina\",\n      \"imageUrl\": \"LINK\",\n      \"position\": \"bottomLeft\"\n    }\n  }" },
                                        new LessonContentDTO { Id = 1007, LessonId = 1002, TypeId = 2, Order = 4, Content = "  {\n    \"title\": {\n      \"en\": \"Creating a Project in Angular\",\n      \"es\": \"Creando un proyecto en Angular\"\n    },\n    \"textContents\": {\n      \"introduction\": [\n        {\n          \"en\": \"Now that we have everything ready, let\'s create a new Angular project using the terminal.\",\n          \"es\": \"Ahora con todo lo necesario vamos a crear un proyecto desde nuestra terminal.\"\n        }\n      ],\n      \"ending\": [\n        {\n          \"en\": \"In the upcoming lessons, we will work with SCSS in our Angular project.\",\n          \"es\": \"Para el proyecto de las siguientes lecciones vamos a trabajar con CSS.\"\n        }\n      ]\n    },\n    \"exercise\": [\n      {\n        \"type\": \"options\",\n        \"completed\": false,\n        \"instruction\": {\n          \"en\": \" \",\n          \"es\": \"Creamos un proyecto. Elige el comando correcto:\"\n        },\n        \"options\": [\n          {\n            \"text\": \"npm create project-name\",\n            \"correct\": false,\n            \"output\": \"Error: Unknown command \'npm create\'.\"\n          },\n          {\n            \"text\": \"ng new project-name\",\n            \"correct\": true,\n            \"output\": \"LINK IMAGEN DE TERMINAL\"\n          },\n          {\n            \"text\": \"ng create project-name\",\n            \"correct\": false,\n            \"output\": \"Error: Unknown command \'ng create\'. Did you mean \'ng add\' ?\"\n          },\n          {\n            \"text\": \"node new project-name\",\n            \"correct\": false,\n            \"output\": \"Error: \'new\' is not a recognized Node.js command.\"\n          }\n        ]\n      }\n    ],\n    \"imageUrl\": \"link\",\n    \"backgroundColor\": \"#FFF3E0\",\n    \"audioUrl\": \"link\",\n    \"avatar\": {\n      \"name\": \"Lina\",\n      \"imageUrl\": \"link\",\n      \"position\": \"bottomLeft\"\n    }\n  }" },
                                        new LessonContentDTO { Id = 1008, LessonId = 1002, TypeId = 2, Order = 3, Content = "{\n    \"title\": {\n      \"en\": \"Quiz: Command for installing Angular CLI\",\n      \"es\": \"Quiz: Comando para la instalación de Angular CLI\"\n    },\n    \"exercise\": [\n      {\n        \"type\": \"options\",\n        \"completed\": false,\n        \"instruction\": {\n          \"en\": \"Install Angular using npm. Choose the correct command:\",\n          \"es\": \"Instalemos Angular con npm. Elige el comando correcto:\"\n        },\n        \"options\": [\n          {\n            \"text\": \"npm install @angular/cli\",\n            \"correct\": false,\n            \"output\": \"Error: Missing global flag `-g`. The Angular CLI won\'t be available as a global command.\"\n          },\n          {\n            \"text\": \"npm install -g @angular/cli\",\n            \"correct\": true,\n            \"output\": \"added 267 packages in 12s\\n\\nchanged 1 package, and audited 267 packages in 5s\\n\\nfound 0 vulnerabilities\\n\\nC:\\\\Users\\\\user\\\\AppData\\\\Roaming\\\\npm\\\\ng -> C:\\\\Users\\\\user\\\\AppData\\\\Roaming\\\\npm\\\\node_modules\\\\@angular\\\\cli\\\\bin\\\\ng\\n\\n+ @angular/cli@18.0.3\"\n          },\n          {\n            \"text\": \"ng install angular\",\n            \"correct\": false,\n            \"output\": \"Error: Unknown command \'install\'. Did you mean \'ng add\' or \'npm install\'?\"\n          },\n          {\n            \"text\": \"node install angular-cli\",\n            \"correct\": false,\n            \"output\": \"Error: \'install\' is not a recognized Node.js command. Use npm to install packages.\"\n          }\n        ]\n      }\n    ],\n    \"imageUrl\": \"LINK\",\n    \"backgroundColor\": \"#FFF3E0\",\n    \"avatar\": {\n      \"name\": \"Lina\",\n      \"imageUrl\": \"LINK\",\n      \"position\": \"bottomLeft\"\n    }\n  }" },
